CSSParser.prototype.parseCharsetRule = function(aSheet) {
  var token = this.getToken(false, false);
  if (token.isAtRule("@charset") && token.value == "@charset") { // lowercase check
    var s = token.value;
    token = this.getToken(false, false);
    s += token.value;
    if (token.isWhiteSpace(" ")) {
      token = this.getToken(false, false);
      s += token.value;
      if (token.isString()) {
        var encoding = token.value;
        token = this.getToken(false, false);
        s += token.value;
        if (token.isSymbol(";")) {
          var rule = new jscsspCharsetRule();
          rule.encoding = encoding;
          rule.parsedCssText = s;
          rule.parentStyleSheet = aSheet;
          aSheet.cssRules.push(rule);
          return true;
        }
        else
          this.reportError(kCHARSET_RULE_MISSING_SEMICOLON);
      }
      else
        this.reportError(kCHARSET_RULE_CHARSET_IS_STRING);
    }
    else
      this.reportError(kCHARSET_RULE_MISSING_WS);
  }
  
  this.addUnknownAtRule(aSheet, s);
  return false;
};

CSSParser.prototype.parseColor = function(token)
{
  var color = "";
  if (token.isFunction("rgb(")
      || token.isFunction("rgba(")) {
    color = token.value;
    var isRgba = token.isFunction("rgba(")
    token = this.getToken(true, true);
    if (!token.isNumber() && !token.isPercentage())
      return "";
    color += token.value;
    token = this.getToken(true, true);
    if (!token.isSymbol(","))
      return "";
    color += ", ";
    
    token = this.getToken(true, true);
    if (!token.isNumber() && !token.isPercentage())
      return "";
    color += token.value;
    token = this.getToken(true, true);
    if (!token.isSymbol(","))
      return "";
    color += ", ";
    
    token = this.getToken(true, true);
    if (!token.isNumber() && !token.isPercentage())
      return "";
    color += token.value;
    
    if (isRgba) {
      token = this.getToken(true, true);
      if (!token.isSymbol(","))
        return "";
      color += ", ";
      
      token = this.getToken(true, true);
      if (!token.isNumber())
        return "";
      color += token.value;
    }
    
    token = this.getToken(true, true);
    if (!token.isSymbol(")"))
      return "";
    color += token.value;
  }
  
  else if (token.isFunction("hsl(")
           || token.isFunction("hsla(")) {
    color = token.value;
    var isHsla = token.isFunction("hsla(")
    token = this.getToken(true, true);
    if (!token.isNumber())
      return "";
    color += token.value;
    token = this.getToken(true, true);
    if (!token.isSymbol(","))
      return "";
    color += ", ";
    
    token = this.getToken(true, true);
    if (!token.isPercentage())
      return "";
    color += token.value;
    token = this.getToken(true, true);
    if (!token.isSymbol(","))
      return "";
    color += ", ";
    
    token = this.getToken(true, true);
    if (!token.isPercentage())
      return "";
    color += token.value;
    
    if (isHsla) {
      token = this.getToken(true, true);
      if (!token.isSymbol(","))
        return "";
      color += ", ";
      
      token = this.getToken(true, true);
      if (!token.isNumber())
        return "";
      color += token.value;
    }
    
    token = this.getToken(true, true);
    if (!token.isSymbol(")"))
      return "";
    color += token.value;
  }
  
  else if (token.isIdent()
           && (token.value in this.kCOLOR_NAMES))
    color = token.value;
  
  else if (token.isSymbol("#")) {
    token = this.getHexValue();
    if (!token.isHex())
      return "";
    var length = token.value.length;
    if (length != 3 && length != 6)
      return "";
    if (token.value.match( /[a-fA-F0-9]/g ).length != length)
      return "";
    color = "#" + token.value;
  }
  return color;
};

CSSParser.prototype.parseCueShorthand = function(token, declarations, aAcceptPriority)
{
  var before = "";
  var after = "";
  
  var values = [];
  var values = [];
  while (true) {
    
    if (!token.isNotNull())
      break;
    
    if (token.isSymbol(";")
        || (aAcceptPriority && token.isSymbol("!"))
        || token.isSymbol("}")) {
      if (token.isSymbol("}"))
        this.ungetToken();
      break;
    }
    
    else if (!values.length && token.isIdent(this.kINHERIT)) {
      values.push(token.value);
    }
    
    else if (token.isIdent("none"))
      values.push(token.value);
    
    else if (token.isFunction("url(")) {
      token = this.getToken(true, true);
      var urlContent = this.parseURL(token);
      if (urlContent)
        values.push("url(" + urlContent);
      else
        return "";
    }
    else
      return "";
    
    token = this.getToken(true, true);
  }
  
  var count = values.length;
  switch (count) {
    case 1:
      before = values[0];
      after = before;
      break;
    case 2:
      before = values[0];
      after = values[1];
      break;
    default:
      return "";
  }
  this.forgetState();
  aDecl.push(this._createJscsspDeclarationFromValue("cue-before", before));
  aDecl.push(this._createJscsspDeclarationFromValue("cue-after", after));
  return before + " " + after;
};

CSSParser.prototype.parseDeclaration = function(aToken, aDecl, aAcceptPriority, aExpandShorthands, aSheet) {
  this.preserveState();
  var blocks = [];
  if (aToken.isIdent()) {
    var descriptor = aToken.value.toLowerCase();
    var token = this.getToken(true, true);
    if (token.isSymbol(":")) {
      var token = this.getToken(true, true);
      
      var value = "";
      var declarations = [];
      if (aExpandShorthands)
        switch (descriptor) {
          case "background":
            value = this.parseBackgroundShorthand(token, declarations, aAcceptPriority);
            break;
          case "margin":
          case "padding":
            value = this.parseMarginOrPaddingShorthand(token, declarations, aAcceptPriority, descriptor);
            break;
          case "border-color":
            value = this.parseBorderColorShorthand(token, declarations, aAcceptPriority);
            break;
          case "border-style":
            value = this.parseBorderStyleShorthand(token, declarations, aAcceptPriority);
            break;
          case "border-width":
            value = this.parseBorderWidthShorthand(token, declarations, aAcceptPriority);
            break;
          case "border-top":
          case "border-right":
          case "border-bottom":
          case "border-left":
          case "border":
          case "outline":
            value = this.parseBorderEdgeOrOutlineShorthand(token, declarations, aAcceptPriority, descriptor);
            break;
          case "cue":
            value = this.parseCueShorthand(token, declarations, aAcceptPriority);
            break;
          case "pause":
            value = this.parsePauseShorthand(token, declarations, aAcceptPriority);
            break;
          case "font":
            value = this.parseFontShorthand(token, declarations, aAcceptPriority);
            break;
          case "list-style":
            value = this.parseListStyleShorthand(token, declarations, aAcceptPriority);
            break;
          default:
            value = this.parseDefaultPropertyValue(token, declarations, aAcceptPriority, descriptor, aSheet);
            break;
        }
      else
        value = this.parseDefaultPropertyValue(token, declarations, aAcceptPriority, descriptor, aSheet);
      token = this.currentToken();
      if (value) // no error above
      {
        var priority = false;
        if (token.isSymbol("!")) {
          token = this.getToken(true, true);
          if (token.isIdent("important")) {
            priority = true;
            token = this.getToken(true, true);
            if (token.isSymbol(";") || token.isSymbol("}")) {
              if (token.isSymbol("}"))
                this.ungetToken();
            }
            else return "";
          }
          else return "";
        }
        else if  (token.isNotNull() && !token.isSymbol(";") && !token.isSymbol("}"))
          return "";
        for (var i = 0; i < declarations.length; i++) {
          declarations[i].priority = priority;
          aDecl.push(declarations[i]);
        }
        return descriptor + ": " + value + ";";
      }
    }
  }
  else if (aToken.isComment()) {
    if (this.mPreserveComments) {
      this.forgetState();
      var comment = new jscsspComment();
      comment.parsedCssText = aToken.value;
      aDecl.push(comment);
    }
    return aToken.value;
  }
  
  // we have an error here, let's skip it
  this.restoreState();
  var s = aToken.value;
  blocks = [];
  var token = this.getToken(false, false);
  while (token.isNotNull()) {
    s += token.value;
    if ((token.isSymbol(";") || token.isSymbol("}")) && !blocks.length) {
      if (token.isSymbol("}"))
        this.ungetToken();
      break;
    } else if (token.isSymbol("{")
               || token.isSymbol("(")
               || token.isSymbol("[")
               || token.isFunction()) {
      blocks.push(token.isFunction() ? "(" : token.value);
    } else if (token.isSymbol("}")
               || token.isSymbol(")")
               || token.isSymbol("]")) {
      if (blocks.length) {
        var ontop = blocks[blocks.length - 1];
        if ((token.isSymbol("}") && ontop == "{")
            || (token.isSymbol(")") && ontop == "(")
            || (token.isSymbol("]") && ontop == "[")) {
          blocks.pop();
        }
      }
    }
    token = this.getToken(false, false);
  }
  return "";
};

